public class JavaTerminology {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Class: The description of an object\n");

		System.out.println("Comment: Text that provides information to the reader of the program code\n");

		System.out.println("Execute: To run a program\n");

		System.out.println("Bytecode: Compiled java source code\n");

		System.out.println("Argument: Data passed to a method for processing\n");
	}

}
