public class TicTacToe {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.format("%7s %6s","|","|\n");
		System.out.format("%7s %6s","|","|\n");
		System.out.format("%7s %6s","|","|\n");
		System.out.format("-------------------\n");
		System.out.format("%7s %6s","|","|\n");
		System.out.format("%7s %2s %3s","|","X","|\n");
		System.out.format("%7s %6s","|","|\n");
		System.out.format("-------------------\n");
		System.out.format("%7s %6s","|","|\n");
		System.out.format("%7s %6s","|","|\n");
		System.out.format("%7s %6s","|","|\n");
	}

}
